  // Easing function for smoother animation
  const easeInOutQuad = (t) => {
    return t < 0.5 ? 2 * t * t : 1 - Math.pow(-2 * t + 2, 2) / 2;
  };

  return (
    <div 
      ref={containerRef}
      onMouseMove={handleMouseMove}
      onClick={requestLock}
      style={{ 
        position: 'fixed', 
        top: 0, 
        left: 0, 
        width: '100%', 
        height: '100%', 
        zIndex: 1000,
        pointerEvents: isPointerLocked ? 'auto' : 'none'
      }}
    >
      {/* Add the SplineScript component which will directly inject a script to control Spline cursor */}
      <SplineScript position={position} />
      
      {/* Visible cursor */}
      <div 
        style={{
          position: 'fixed',
          left: position.x,
          top: position.y,
          width: '50px',
          height: '50px',
          borderRadius: '50%',
          backgroundColor: 'rgba(255, 0, 0, 0.8)',
          border: '3px solid white',
          transform: 'translate(-50%, -50%)',
          pointerEvents: 'none',
          zIndex: 9999,
          boxShadow: '0 0 20px rgba(255, 0, 0, 1)'
        }}
      >
        <div style={{ 
          position: 'absolute', 
          top: '55px', 
          left: '50%', 
          transform: 'translateX(-50%)',
          background: 'black',
          color: 'white',
          padding: '5px 10px',
          borderRadius: '5px',
          fontSize: '14px',
          fontWeight: 'bold',
          whiteSpace: 'nowrap'
        }}>
          {isPointerLocked ? `MANUAL (${mouseSensitivityRef.current.toFixed(1)}x)` : currentPatternRef.current + ' - AUTO'}
        </div>
        <div style={{
          position: 'absolute',
          width: '10px',
          height: '10px',
          background: 'white',
          borderRadius: '50%',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)'
        }} />
      </div>
      
      {/* Instructions overlay when not pointer locked */}
      {!isPointerLocked && (
        <div style={{
          position: 'fixed',
          bottom: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          color: 'white',
          padding: '10px 20px',
          borderRadius: '5px',
          textAlign: 'center',
          zIndex: 9998
        }}>
          Click anywhere to take manual control of the cursor
        </div>
      )}
      
      {/* Controls help when pointer is locked */}
      {isPointerLocked && (
        <div style={{
          position: 'fixed',
          bottom: '20px',
          left: '50%',
          transform: 'translateX(-50%)',
          backgroundColor: 'rgba(0, 0, 0, 0.7)',
          color: 'white',
          padding: '10px 20px',
          borderRadius: '5px',
          textAlign: 'center',
          zIndex: 9998,
          fontSize: '14px'
        }}>
          <strong>Controls:</strong> Press ESC to exit | +/- to adjust sensitivity
        </div>
      )}
    </div>
  );
};

export default FakeCursor;
