import React, { useRef, useState, useEffect } from "react";
import "@tensorflow/tfjs";
import "@tensorflow/tfjs-backend-webgl";
import Webcam from "react-webcam";
import FaceDirectionDetector from "./FaceDirectionDetector";

const inputResolution = {
  width: 640,
  height: 480,
};

const videoConstraints = {
  width: inputResolution.width,
  height: inputResolution.height,
  facingMode: "user",
};

// Thresholds for determining direction
const DIRECTION_THRESHOLDS = {
  LEFT: 30,  // Degrees for yaw to trigger left
  RIGHT: 30, // Degrees for yaw to trigger right
  UP: 30,    // Degrees for tilt to trigger up
  DOWN: 20,  // Degrees for tilt to trigger down
  NEUTRAL_ZONE: 15, // Degrees of center zone where no direction is triggered
  // X-coordinate thresholds (pixels from center)
  X_LEFT: 200,  // X < center - X_LEFT triggers left
  X_RIGHT: 400  // X > center + X_RIGHT triggers right
};

// Cooldown between direction changes in milliseconds
const DIRECTION_COOLDOWN = 300;

function FaceDirectionComponent({ onDirectionChange }) {
  const canvasRef = useRef(null);
  const webcamRef = useRef(null);
  const [loaded, setLoaded] = useState(false);
  const [detector, setDetector] = useState(null);
  const [facePosition, setFacePosition] = useState({ x: 0, y: 0 });
  const [currentDirection, setCurrentDirection] = useState("center");
  const lastDirectionTimeRef = useRef(Date.now());
  const processingActiveRef = useRef(false);
  const centerPositionRef = useRef({ x: inputResolution.width / 2, y: inputResolution.height / 2 });
  
  // Options for detector
  const [options] = useState({
    showMesh: false,
    showTagNumbers: false,
    showDirection: false
  });

  // Initialize the detector
  useEffect(() => {
    const initializeDetector = async () => {
      try {
        // Ensure TensorFlow backend is ready
        await window.tf.ready();
        console.log("TensorFlow backend ready:", window.tf.getBackend());
        
        const faceDetector = new FaceDirectionDetector(options);
        setDetector(faceDetector);
      } catch (error) {
        console.error("Error initializing face detector:", error);
      }
    };

    initializeDetector();

    // Cleanup on unmount
    return () => {
      if (detector) {
        detector.stop();
      }
    };
  }, [options]);

  // Determine direction based on face position and angles
  const determineDirection = (data) => {
    if (!data || !data.keypoints || !data.keypoints[1]) {
      return "center";
    }
    
    const now = Date.now();
    // Check if we're still in cooldown period
    if (now - lastDirectionTimeRef.current < DIRECTION_COOLDOWN) {
      return currentDirection;
    }
    
    const { yaw, turn } = data;
    const noseX = data.keypoints[1].x;
    const centerX = centerPositionRef.current.x;
    
    // Determine horizontal direction (left/right)
    // Use both yaw angle and X position
    let newDirection = "center";
    
    // First check based on absolute position
    const xDiff = noseX - centerX;
    
    if (xDiff < -DIRECTION_THRESHOLDS.X_LEFT) {
      newDirection = "left";
    } else if (xDiff > DIRECTION_THRESHOLDS.X_RIGHT) {
      newDirection = "right";
    } else if (yaw > DIRECTION_THRESHOLDS.RIGHT) {
      // If X position isn't clear enough, use yaw as backup
      newDirection = "right";
    } else if (yaw < -DIRECTION_THRESHOLDS.LEFT) {
      newDirection = "left";
    }
    
    // Turn affects up/down - this is approximate since the model doesn't directly give tilt
    // We use the nose position relative to the face center
    if (turn > DIRECTION_THRESHOLDS.UP) {
      newDirection = "up";
    } else if (turn < -DIRECTION_THRESHOLDS.DOWN) {
      newDirection = "down";
    }
    
    // Only update if direction changed
    if (newDirection !== currentDirection) {
      lastDirectionTimeRef.current = now;
      return newDirection;
    }
    
    return currentDirection;
  };

  // Map direction to key
  const directionToKey = (direction) => {
    switch (direction) {
      case "left":
        return "l";
      case "right":
        return "r"; 
      case "up":
        return "u";
      case "down":
        return "d";
      default:
        return null;
    }
  };

  // Process video frames
  const processVideo = async () => {
    if (processingActiveRef.current || !detector || !webcamRef.current || !canvasRef.current) {
      return;
    }

    try {
      // Check if video is actually playing and has dimensions
      const video = webcamRef.current.video;
      
      if (!video || !video.readyState || video.readyState < 2 || 
          video.videoWidth === 0 || video.videoHeight === 0) {
        // Video not ready yet, retry later
        setTimeout(processVideo, 100);
        return;
      }

      // Mark as processing to prevent concurrent processing
      processingActiveRef.current = true;
      
      // Update center position reference based on actual video dimensions
      centerPositionRef.current = {
        x: video.videoWidth / 2,
        y: video.videoHeight / 2
      };
      
      // Process with detector
      await detector.start(
        video,
        canvasRef.current,
        (data) => {
          // Extract face position from nose tip (keypoint 1)
          if (data && data.keypoints && data.keypoints[1]) {
            const newPos = {
              x: Math.round(data.keypoints[1].x),
              y: Math.round(data.keypoints[1].y)
            };
            setFacePosition(newPos);
            
            // Determine direction
            const newDirection = determineDirection(data);
            if (newDirection !== currentDirection) {
              setCurrentDirection(newDirection);
              
              // Map direction to key
              const key = directionToKey(newDirection);
              if (key && onDirectionChange) {
                onDirectionChange(key);
              }
            }
          }
          
          // Mark processing as completed
          processingActiveRef.current = false;
        }
      );
      
      // Set as loaded to prevent reinitialization
      setLoaded(true);
    } catch (error) {
      console.error("Error processing video:", error);
      processingActiveRef.current = false;
      // Retry after an error with a delay
      setTimeout(processVideo, 1000);
    }
  };

  // Handle video load
  const handleVideoLoad = () => {
    if (loaded) return;
    
    // Start processing with a slight delay to ensure video is fully loaded
    setTimeout(processVideo, 500);
  };

  // Effect to ensure cleanup on unmount
  useEffect(() => {
    return () => {
      if (detector) {
        detector.stop();
      }
      processingActiveRef.current = false;
    };
  }, [detector]);

  return (
    <div className="face-direction-container">
      {/* Video and canvas for processing - now visible for debugging but transparent */}
      <div style={{ 
        position: "absolute", 
        opacity: 0.1, 
        zIndex: 500,
        pointerEvents: "none",
        bottom: "80px",
        right: "20px",
        width: "160px",
        height: "120px",
        overflow: "hidden"
      }}>
        <Webcam
          ref={webcamRef}
          width={inputResolution.width}
          height={inputResolution.height}
          videoConstraints={videoConstraints}
          onLoadedData={handleVideoLoad}
          style={{ width: "100%", height: "100%", objectFit: "cover" }}
        />
        
        <canvas
          ref={canvasRef}
          width={inputResolution.width}
          height={inputResolution.height}
          style={{ 
            position: "absolute", 
            top: 0, 
            left: 0,
            width: "100%", 
            height: "100%"
          }}
        />
      </div>

      {/* Display current face position */}
      <div className="face-position-display">
        <div className="direction-indicator">
          <div className={`direction-arrow ${currentDirection}`}>
            {currentDirection !== "center" && "→"}
          </div>
          <div className="coordinates">
            {facePosition.x},{facePosition.y}
          </div>
        </div>
      </div>
    </div>
  );
}

export default FaceDirectionComponent;
