import React, { useState, useEffect } from 'react';
import SplineViewer from './SplineViewer';

const LoadingTexts = [
  "Loading up bot",
  "Initialising AI brain",
  "Connecting all nerves",
  "Booting up essential organs"
];

const SplineLoader = ({ onLoadingComplete, showStatus = true }) => {
  const [currentLoadingPhase, setCurrentLoadingPhase] = useState(0);
  const [isInitialLoading, setIsInitialLoading] = useState(true);
  const [loadingText, setLoadingText] = useState(LoadingTexts[0]);
  const [textOpacity, setTextOpacity] = useState(1);

  // Handle loading text animation
  useEffect(() => {
    if (!isInitialLoading) return;

    const textInterval = setInterval(() => {
      setTextOpacity(0);
      
      setTimeout(() => {
        setCurrentLoadingPhase(prev => {
          const nextPhase = prev + 1;
          if (nextPhase >= LoadingTexts.length) {
            clearInterval(textInterval);
            return prev;
          }
          setLoadingText(LoadingTexts[nextPhase]);
          setTextOpacity(1);
          return nextPhase;
        });
      }, 500); // Wait for fade out before changing text
    }, 1200); // Change text every 1.2 seconds
    
    // Handle transition to main scene after 5 seconds
    const loadingTimer = setTimeout(() => {
      setIsInitialLoading(false);
      if (onLoadingComplete) onLoadingComplete();
    }, 5000);
    
    return () => {
      clearInterval(textInterval);
      clearTimeout(loadingTimer);
    };
  }, [isInitialLoading, onLoadingComplete]);

  return (
    <div style={{ 
      position: 'relative', 
      width: '100%', 
      height: '100vh',
      overflow: 'hidden',
      backgroundColor: '#000'
    }}>
      {/* Initial loading animation */}
      {isInitialLoading && (
        <>
          <div 
            style={{ 
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              zIndex: 1
            }}
          >
            <SplineViewer 
              url="https://prod.spline.design/xpxDmF5OWl8sWQlZ/scene.splinecode" 
            />
          </div>
          <div 
            style={{ 
              position: 'absolute',
              top: '20%',
              left: 0,
              width: '100%',
              textAlign: 'center',
              color: 'white',
              fontSize: '2rem',
              fontWeight: 'bold',
              zIndex: 2,
              opacity: textOpacity,
              transition: 'opacity 0.5s ease'
            }}
          >
            {loadingText}
          </div>
        </>
      )}
      
      {/* Main scene after loading */}
      {!isInitialLoading && (
        <div 
          style={{ 
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
          }}
        >
          <SplineViewer 
            url="https://prod.spline.design/8CIsvX5hg8u-JUKM/scene.splinecode"
            options={{
              hint: true,
              'loading-anim-type': 'spinner-small-dark'
            }}
          />
          
          {showStatus && (
            <div style={{ 
              position: 'absolute', 
              bottom: '20px', 
              right: '20px',
              background: 'rgba(0, 0, 0, 0.7)',
              color: 'white',
              padding: '10px',
              borderRadius: '5px',
              maxWidth: '300px'
            }}>
              <h3 style={{ margin: '0 0 10px 0' }}>Status Information</h3>
              <div>
                <p style={{ margin: '5px 0' }}>System: Online</p>
                <p style={{ margin: '5px 0' }}>Connection: Stable</p>
                <p style={{ margin: '5px 0' }}>Ready for interaction</p>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SplineLoader;
