import React, { useEffect, useRef, useState } from 'react';

// Animation patterns for different talking states
const TALKING_PATTERNS = {
  normal: [
    { x: 0.5, y: 0.5, duration: 1000 }, // center
    { x: 0.5, y: 0.3, duration: 800 },  // look up
    { x: 0.5, y: 0.5, duration: 700 },  // center
    { x: 0.3, y: 0.5, duration: 900 },  // look left
    { x: 0.5, y: 0.5, duration: 600 },  // center
    { x: 0.7, y: 0.5, duration: 1000 }, // look right
    { x: 0.5, y: 0.5, duration: 700 },  // center
    { x: 0.5, y: 0.7, duration: 800 },  // look down
  ],
  
  nodding: [
    { x: 0.5, y: 0.4, duration: 600 },  // up slightly
    { x: 0.5, y: 0.6, duration: 600 },  // down slightly
    { x: 0.5, y: 0.4, duration: 600 },  // up slightly
    { x: 0.5, y: 0.6, duration: 600 },  // down slightly
  ],
  
  shaking: [
    { x: 0.4, y: 0.5, duration: 400 },  // left
    { x: 0.6, y: 0.5, duration: 400 },  // right
    { x: 0.4, y: 0.5, duration: 400 },  // left
    { x: 0.6, y: 0.5, duration: 400 },  // right
  ]
};

const FakeCursor = ({ isTalking, isUserInteracting, debug = false }) => {
  // Always make cursor visible
  const [isVisible, setIsVisible] = useState(true);
  const [position, setPosition] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });
  const animationFrameRef = useRef(null);
  const patternIndexRef = useRef(0);
  const patternTimeRef = useRef(0);
  const currentPatternRef = useRef('normal');
  
  // Log component mounting for debugging
  useEffect(() => {
    console.log("FakeCursor component mounted");
    console.log("Initial position:", position);
    console.log("Debug mode:", debug);
    console.log("isTalking:", isTalking);
    
    return () => {
      console.log("FakeCursor component unmounted");
    };
  }, []);
  
  // Random pattern selection for more natural movement
  const selectRandomPattern = () => {
    const patterns = ['normal', 'nodding', 'shaking'];
    const randomIndex = Math.floor(Math.random() * patterns.length);
    const selected = patterns[randomIndex];
    console.log("Selected pattern:", selected);
    return selected;
  };

  // Animate the fake cursor regardless of talking state (for testing)
  useEffect(() => {
    console.log("Starting animation loop");
    setIsVisible(true);
    
    // Start with a random pattern
    if (!animationFrameRef.current) {
      currentPatternRef.current = 'normal'; // Start with normal pattern for testing
      patternIndexRef.current = 0;
      patternTimeRef.current = 0;
    }
    
    const animateCursor = (timestamp) => {
      if (!patternTimeRef.current) {
        patternTimeRef.current = timestamp;
      }
      
      const pattern = TALKING_PATTERNS[currentPatternRef.current];
      const targetPoint = pattern[patternIndexRef.current];
      const elapsed = timestamp - patternTimeRef.current;
      
      if (elapsed >= targetPoint.duration) {
        // Move to next point in pattern
        patternIndexRef.current = (patternIndexRef.current + 1) % pattern.length;
        
        // Change patterns occasionally
        if (patternIndexRef.current === 0) {
          // For testing, cycle through patterns in a fixed order
          if (currentPatternRef.current === 'normal') {
            currentPatternRef.current = 'nodding';
          } else if (currentPatternRef.current === 'nodding') {
            currentPatternRef.current = 'shaking';
          } else {
            currentPatternRef.current = 'normal';
          }
          console.log("Changed to pattern:", currentPatternRef.current);
        }
        
        patternTimeRef.current = timestamp;
      }
      
      // Calculate current position based on pattern
      const previousIndex = patternIndexRef.current === 0 ? pattern.length - 1 : patternIndexRef.current - 1;
      const previousTarget = pattern[previousIndex];
      const nextTarget = pattern[patternIndexRef.current];
      const progress = Math.min(elapsed / nextTarget.duration, 1);
      
      // Apply easing for smoother movement
      const easeProgress = easeInOutQuad(progress);
      
      // Calculate new position
      const targetX = window.innerWidth * nextTarget.x;
      const targetY = window.innerHeight * nextTarget.y;
      const previousX = window.innerWidth * previousTarget.x;
      const previousY = window.innerHeight * previousTarget.y;
      
      const newX = previousX + (targetX - previousX) * easeProgress;
      const newY = previousY + (targetY - previousY) * easeProgress;
      
      setPosition({
        x: newX,
        y: newY
      });
      
      // Log every 60 frames (about once per second)
      if (Math.random() < 0.01) {
        console.log("Current cursor position:", newX, newY);
        console.log("Current pattern:", currentPatternRef.current);
      }
      
      // Dispatch synthetic mouse event
      const event = new MouseEvent('mousemove', {
        view: window,
        bubbles: true,
        cancelable: true,
        clientX: newX,
        clientY: newY
      });
      document.dispatchEvent(event);
      
      animationFrameRef.current = requestAnimationFrame(animateCursor);
    };
    
    animationFrameRef.current = requestAnimationFrame(animateCursor);
    
    return () => {
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
        animationFrameRef.current = null;
        console.log("Animation loop stopped");
      }
    };
  }, []); // Empty dependency array to run once

  // Easing function for smoother animation
  const easeInOutQuad = (t) => {
    return t < 0.5 ? 2 * t * t : 1 - Math.pow(-2 * t + 2, 2) / 2;
  };

  // ALWAYS render the cursor for testing purposes
  return (
    <div 
      style={{
        position: 'fixed',
        left: position.x,
        top: position.y,
        width: '50px',
        height: '50px',
        borderRadius: '50%',
        backgroundColor: 'rgba(255, 0, 0, 0.8)',
        border: '3px solid white',
        transform: 'translate(-50%, -50%)',
        pointerEvents: 'none',
        zIndex: 9999,
        boxShadow: '0 0 20px rgba(255, 0, 0, 1)'
      }}
    >
      <div style={{ 
        position: 'absolute', 
        top: '55px', 
        left: '50%', 
        transform: 'translateX(-50%)',
        background: 'black',
        color: 'white',
        padding: '5px 10px',
        borderRadius: '5px',
        fontSize: '14px',
        fontWeight: 'bold',
        whiteSpace: 'nowrap'
      }}>
        {currentPatternRef.current} - TESTING
      </div>
      <div style={{
        position: 'absolute',
        width: '10px',
        height: '10px',
        background: 'white',
        borderRadius: '50%',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)'
      }} />
    </div>
  );
};

export default FakeCursor;
