    ctx.fillStyle = "purple";
    ctx.beginPath();
    ctx.arc(midpoint.x, midpoint.y, 3, 0, 2 * Math.PI);
    ctx.fill();
  }

  /**
   * Calculate distance between two points
   * @param {Object} point1 - First point with x, y, z coordinates
   * @param {Object} point2 - Second point with x, y, z coordinates
   * @returns {number} Distance between the points
   */
  getDistanceBetweenPoints(point1, point2) {
    const dx = point1.x - point2.x;
    const dy = point1.y - point2.y;
    const dz = (point1.z || 0) - (point2.z || 0);
    return Math.sqrt(dx * dx + dy * dy + dz * dz);
  }

  /**
   * Calculate angle between two lines with a common point
   * @param {Object} midpoint - Common point for both lines
   * @param {Object} point1 - End point of first line
   * @param {Object} point2 - End point of second line
   * @returns {number} Angle in degrees
   */
  getAngleBetweenLines(midpoint, point1, point2) {
    const angle1 = Math.atan2(point1.y - midpoint.y, point1.x - midpoint.x);
    const angle2 = Math.atan2(point2.y - midpoint.y, point2.x - midpoint.x);
    let angle = (angle1 - angle2) * (180 / Math.PI);
    
    // Normalize the angle
    if (angle < 0) {
      angle += 360;
    }
    if (angle > 180) {
      angle = 360 - angle;
    }
    
    return angle;
  }
}

export default FaceDirectionDetector;
