import React, { useState, useEffect, useRef } from 'react';
import { RetellWebClient } from "retell-client-js-sdk";
import SplineLoader from './SplineLoader';
import SplineViewer from './SplineViewer';
import './index.css';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import agentIcon from './assets/agent-icon.svg';
import userIcon from './assets/user-icon.svg';
// Import face-api.js for face tracking
import * as faceapi from 'face-api.js';

function App() {
  // Add a ref to maintain the Spline instance
  const splineInstanceRef = useRef(null);
  const [isCallActive, setIsCallActive] = useState(false);
  const [retellWebClient, setRetellWebClient] = useState(null);
  const [eventStatus, setEventStatus] = useState({
    callStarted: false,
    callEnded: false,
    agentStartTalking: false,
    agentStopTalking: false,
    error: null,
    transcript: ''
  });
  const [micPermission, setMicPermission] = useState(null);
  const [accessToken, setAccessToken] = useState('');
  const [isGeneratingToken, setIsGeneratingToken] = useState(false);
  const [callInfo, setCallInfo] = useState(null);
  const [loadingComplete, setLoadingComplete] = useState(false);
  const [showUI, setShowUI] = useState(true);
  // Add state to control the mounting/unmounting of Spline
  const [splineInitialized, setSplineInitialized] = useState(false);
  // Add refs for animation control
  const animationTimerRef = useRef(null);
  const lastKeyPressRef = useRef(null);
  // Track if key is being pressed
  const keyPressedRef = useRef({});
  // State for graph visualization
  const [showGraphs, setShowGraphs] = useState(false);
  const [graphType, setGraphType] = useState('bar'); // 'bar', 'line'
  const graphTimerRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [isAgentSpeaking, setIsAgentSpeaking] = useState(false);
  const [transcript, setTranscript] = useState('');
  const graphKeywords = ['graph', 'chart', 'stats', 'data', 'visualization', 'metrics', 'numbers'];
  const graphIntervalRef = useRef(null);
  const messagesEndRef = useRef(null);
  // State to track if graphs have been animated
  const [graphsAnimated, setGraphsAnimated] = useState(false);
  // State for active tab (voice, finance, connect)
  const [activeTab, setActiveTab] = useState('voice');

  // Face tracking refs and state
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [faceTrackingActive, setFaceTrackingActive] = useState(false);
  const [facePosition, setFacePosition] = useState({ x: 0, y: 0 });
  const faceDetectionInterval = useRef(null);
  const [cameraPermission, setCameraPermission] = useState(null);
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const detectionTimeoutRef = useRef(null);

  const {
    transcript: currentTranscript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  // Key press duration in milliseconds (1 second)
  const KEY_PRESS_DURATION = 1000; 

  // Agent ID for Retell
  const AGENT_ID = "agent_9472fc0c61886098d4a311f2a8";

  // Server endpoint for token generation
  const TOKEN_SERVER_URL = "http://127.0.0.1:5000/api/generate-token";

  // Load face-api.js models
  const loadFaceDetectionModels = async () => {
    try {
      // Specify the models directory relative to your public folder
      const MODEL_URL = '/models';

      // Load required face-api.js models
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);

      console.log('Face detection models loaded successfully');
      setModelsLoaded(true);
    } catch (error) {
      console.error('Error loading face detection models:', error);
    }
  };

  // Request camera permission
  const requestCameraPermission = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });

      // Set video stream to video element
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }

      setCameraPermission('granted');
      return stream;
    } catch (error) {
      console.error('Camera permission denied:', error);
      setCameraPermission('denied');
      return null;
    }
  };

  // Start face tracking
  const startFaceTracking = async () => {
    if (!modelsLoaded) {
      await loadFaceDetectionModels();
    }

    // Request camera permission if not already granted
    if (cameraPermission !== 'granted') {
      const stream = await requestCameraPermission();
      if (!stream) return;
    }

    // Set face tracking active
    setFaceTrackingActive(true);

    // Clear any existing interval
    if (faceDetectionInterval.current) {
      clearInterval(faceDetectionInterval.current);
    }

    // Set up face detection interval
    faceDetectionInterval.current = setInterval(async () => {
      detectFaces();
    }, 100); // Detect faces every 100ms
  };

  // Stop face tracking
  const stopFaceTracking = () => {
    // Clear face detection interval
    if (faceDetectionInterval.current) {
      clearInterval(faceDetectionInterval.current);
      faceDetectionInterval.current = null;
    }

    // Clear timeout if any
    if (detectionTimeoutRef.current) {
      clearTimeout(detectionTimeoutRef.current);
      detectionTimeoutRef.current = null;
    }

    // Stop all video tracks
    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }

    setFaceTrackingActive(false);
  };

  // Detect faces function
  const detectFaces = async () => {
    if (!videoRef.current || !canvasRef.current || !faceTrackingActive || !modelsLoaded) return;

    // Make sure video is ready
    if (videoRef.current.readyState !== 4) return;

    // Get video dimensions
    const videoWidth = videoRef.current.videoWidth;
    const videoHeight = videoRef.current.videoHeight;

    // Set canvas dimensions to match video
    canvasRef.current.width = videoWidth;
    canvasRef.current.height = videoHeight;

    // Detect faces with TinyFaceDetector (faster than SSD)
    const detections = await faceapi.detectAllFaces(
      videoRef.current, 
      new faceapi.TinyFaceDetectorOptions()
    ).withFaceLandmarks();

    // Clear canvas
    const ctx = canvasRef.current.getContext('2d');
    ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);

    // Draw results on canvas for visualization
    faceapi.draw.drawDetections(canvasRef.current, detections);
    faceapi.draw.drawFaceLandmarks(canvasRef.current, detections);

    // If face detected, track position
    if (detections.length > 0) {
      // Get the first detected face
      const detection = detections[0];

      // Calculate center position of face relative to video dimensions
      const x = detection.detection.box.x + (detection.detection.box.width / 2);
      const y = detection.detection.box.y + (detection.detection.box.height / 2);

      // Calculate face position relative to center of video (-1 to 1 range)
      const relX = (x / videoWidth) * 2 - 1;  // -1 (left) to 1 (right)
      const relY = (y / videoHeight) * 2 - 1; // -1 (top) to 1 (bottom)

      // Update face position state
      setFacePosition({ x: relX, y: relY });

      // Use face position to control Spline animation
      controlSplineWithFace(relX, relY);
    }
  };

  // Control Spline animation based on face position
  const controlSplineWithFace = (relX, relY) => {
    // Use horizontal position for left/right controls
    // Use vertical position for up/down controls

    // Create dead zone in the center (-0.2 to 0.2) to avoid constant head movement
    const deadZone = 0.2;

    // Clear any existing detection timeout
    if (detectionTimeoutRef.current) {
      clearTimeout(detectionTimeoutRef.current);
    }

    // Horizontal movement (left/right)
    if (relX < -deadZone) {
      simulateKeyPress('l', true); // Press 'l' for left movement
      releaseKey('r'); // Release right key if pressed
    } else if (relX > deadZone) {
      simulateKeyPress('r', true); // Press 'r' for right movement
      releaseKey('l'); // Release left key if pressed
    } else {
      // In horizontal dead zone, release both left and right keys
      releaseKey('l');
      releaseKey('r');
    }

    // Vertical movement (up/down)
    if (relY < -deadZone) {
      simulateKeyPress('u', true); // Press 'u' for up movement
      releaseKey('d'); // Release down key if pressed
    } else if (relY > deadZone) {
      simulateKeyPress('d', true); // Press 'd' for down movement
      releaseKey('u'); // Release up key if pressed
    } else {
      // In vertical dead zone, release both up and down keys
      releaseKey('u');
      releaseKey('d');
    }

    // Set a timeout to release all keys if no face detected for a while
    detectionTimeoutRef.current = setTimeout(() => {
      releaseKey('l');
      releaseKey('r');
      releaseKey('u');
      releaseKey('d');
    }, 500);
  };

  // Effect to load face detection models when component mounts
  useEffect(() => {
    loadFaceDetectionModels();

    // Clean up face tracking on unmount
    return () => {
      stopFaceTracking();
    };
  }, []);

  // Function to check if the transcript contains data visualization keywords
  const checkForGraphKeywords = (transcript) => {
    if (!transcript) return false;

    const lowerText = transcript.toLowerCase();
    return (
      lowerText.includes('chart') || 
      lowerText.includes('graph') || 
      lowerText.includes('stats') ||
      lowerText.includes('statistics') ||
      lowerText.includes('visualization') ||
      lowerText.includes('plot') ||
      lowerText.includes('diagram')
    );
  };

  // Function to handle graph visualization
  const handleGraphVisualization = (transcript) => {
    const shouldShowGraphs = checkForGraphKeywords(transcript);

    if (shouldShowGraphs && !showGraphs) {
      // Show graphs when keywords are detected
      setShowGraphs(true);

      // Randomly select a graph type to display
      const graphTypes = ['bar', 'line'];
      const randomType = graphTypes[Math.floor(Math.random() * graphTypes.length)];
      setGraphType(randomType);

      // Cycle through graph types while keywords are present
      if (graphTimerRef.current) {
        clearInterval(graphTimerRef.current);
      }

