          key: key,
          code: `Key${key.toUpperCase()}`,
          keyCode: key.charCodeAt(0),
          which: key.charCodeAt(0),
          bubbles: true,
          cancelable: true,
          composed: true
        });
        document.activeElement.dispatchEvent(event);

        // Schedule keyup after KEY_PRESS_DURATION
        setTimeout(() => {
          const upEvent = new KeyboardEvent('keyup', {
            key: key,
            code: `Key${key.toUpperCase()}`,
            keyCode: key.charCodeAt(0),
            which: key.charCodeAt(0),
            bubbles: true,
            cancelable: true,
            composed: true
