import React, { useRef, useState, useEffect } from "react";
import "@tensorflow/tfjs";
import "@tensorflow/tfjs-backend-webgl";
import "@mediapipe/face_mesh";
import Webcam from "react-webcam";
import FaceDirectionDetector from "./face_detection/FaceDirectionDetector";

const inputResolution = {
  width: 640,
  height: 480,
};

const videoConstraints = {
  width: inputResolution.width,
  height: inputResolution.height,
  facingMode: "user",
};

function FaceDirectionComponent() {
  const canvasRef = useRef(null);
  const webcamRef = useRef(null);
  const [loaded, setLoaded] = useState(false);
  const [detector, setDetector] = useState(null);
  const [faceData, setFaceData] = useState({
    position: { x: 0, y: 0 },
    direction: { yaw: 0, turn: 0 },
    horizontalDirection: "center",
    verticalDirection: "center"
  });
  const lastHorizontalDirectionRef = useRef("center");
  const lastVerticalDirectionRef = useRef("center");
  const horizontalKeyPressTimerRef = useRef(null);
  const verticalKeyPressTimerRef = useRef(null);

  // Initialize the detector
  useEffect(() => {
    const initializeDetector = async () => {
      const options = {
        showMesh: false,
        showTagNumbers: false,
        showDirection: false
      };
      const faceDetector = new FaceDirectionDetector(options);
      setDetector(faceDetector);
    };

    initializeDetector();

    // Cleanup on unmount
    return () => {
      if (detector) {
        detector.stop();
      }
      // Clear any active timers
      if (horizontalKeyPressTimerRef.current) {
        clearInterval(horizontalKeyPressTimerRef.current);
      }
      if (verticalKeyPressTimerRef.current) {
        clearInterval(verticalKeyPressTimerRef.current);
      }
    };
  }, []);

  // Determine face direction based on x coordinate
  const determineHorizontalDirection = (x) => {
    if (x < 250) {
      return "left";
    } else if (x > 450) {
      return "right";
    } else {
      return "center";
    }
  };

  // Determine face direction based on y coordinate
  const determineVerticalDirection = (y) => {
    if (y < 200) {
      return "up";
    } else if (y > 320) {
      return "down";
    } else {
      return "center";
    }
  };

  // Simulate keyboard press
  const simulateKeyPress = (key) => {
    try {
      // Create keydown event
      const keyDownEvent = new KeyboardEvent('keydown', {
        key: key,
        code: `Key${key.toUpperCase()}`,
        keyCode: key.charCodeAt(0),
        which: key.charCodeAt(0),
        bubbles: true,
        cancelable: true
      });

      // Dispatch the event
      document.dispatchEvent(keyDownEvent);

      // Log the key press
      console.log(`Key ${key} pressed based on face direction`);

      // Create and dispatch keyup event after a short delay
      setTimeout(() => {
        const keyUpEvent = new KeyboardEvent('keyup', {
          key: key,
          code: `Key${key.toUpperCase()}`,
          keyCode: key.charCodeAt(0),
          which: key.charCodeAt(0),
          bubbles: true,
          cancelable: true
        });
        document.dispatchEvent(keyUpEvent);
      }, 100);
    } catch (error) {
      console.error("Error simulating key press:", error);
    }
  };

  // Effect to handle horizontal direction changes and trigger key presses
  useEffect(() => {
    // If direction changed, trigger appropriate key press
    if (faceData.horizontalDirection !== lastHorizontalDirectionRef.current) {
      lastHorizontalDirectionRef.current = faceData.horizontalDirection;

      // Clear any existing timer
      if (horizontalKeyPressTimerRef.current) {
        clearInterval(horizontalKeyPressTimerRef.current);
        horizontalKeyPressTimerRef.current = null;
      }

      // If looking left or right, start continuous key pressing
      if (faceData.horizontalDirection === "left") {
        simulateKeyPress('l');
        horizontalKeyPressTimerRef.current = setInterval(() => {
          simulateKeyPress('l');
        }, 300); // Repeat every 300ms
      } 
      else if (faceData.horizontalDirection === "right") {
        simulateKeyPress('r');
        horizontalKeyPressTimerRef.current = setInterval(() => {
          simulateKeyPress('r');
        }, 300); // Repeat every 300ms
      }
      // Center - no key press needed
    }
  }, [faceData.horizontalDirection]);

  // Effect to handle vertical direction changes and trigger key presses
  useEffect(() => {
    // If direction changed, trigger appropriate key press
    if (faceData.verticalDirection !== lastVerticalDirectionRef.current) {
      lastVerticalDirectionRef.current = faceData.verticalDirection;

      // Clear any existing timer
      if (verticalKeyPressTimerRef.current) {
        clearInterval(verticalKeyPressTimerRef.current);
        verticalKeyPressTimerRef.current = null;
      }

      // If looking up or down, start continuous key pressing
      if (faceData.verticalDirection === "up") {
        simulateKeyPress('u');
        verticalKeyPressTimerRef.current = setInterval(() => {
          simulateKeyPress('u');
        }, 300); // Repeat every 300ms
      } 
      else if (faceData.verticalDirection === "down") {
        simulateKeyPress('d');
        verticalKeyPressTimerRef.current = setInterval(() => {
          simulateKeyPress('d');
        }, 300); // Repeat every 300ms
      }
      // Center - no key press needed
    }
  }, [faceData.verticalDirection]);

  // Handle video load
  const handleVideoLoad = async (videoNode) => {
    const video = videoNode.target;
    if (video.readyState !== 4) return;
    if (loaded) return;

    if (detector && webcamRef.current && canvasRef.current) {
      await detector.start(
        webcamRef.current.video, 
        canvasRef.current, 
        (data) => {
          // Update face data when detection occurs
          if (data && data.keypoints && data.keypoints[1]) {
            const x = Math.round(data.keypoints[1].x);
            const y = Math.round(data.keypoints[1].y);
            const horizontalDirection = determineHorizontalDirection(x);
            const verticalDirection = determineVerticalDirection(y);

            setFaceData({
              position: { x, y },
              direction: {
                yaw: Math.round(data.yaw || 0),
                turn: Math.round(data.turn || 0)
              },
              horizontalDirection,
              verticalDirection
            });
          }
        }
      );
      setLoaded(true);
    }
  };

  // Helper function to get key press text for display
  const getKeyPressText = () => {
    const keys = [];

    if (faceData.horizontalDirection === "left") keys.push("'l'");
    if (faceData.horizontalDirection === "right") keys.push("'r'");
    if (faceData.verticalDirection === "up") keys.push("'u'");
    if (faceData.verticalDirection === "down") keys.push("'d'");

    if (keys.length === 0) return " (no key press)";
    return ` (pressing ${keys.join(", ")})`;
  };

  return (
    <div>
      {/* Hidden webcam and canvas for detection */}
      <div style={{ position: "absolute", visibility: "hidden" }}>
        <Webcam
          ref={webcamRef}
          width={inputResolution.width}
          height={inputResolution.height}
          videoConstraints={videoConstraints}
          onLoadedData={handleVideoLoad}
        />

        <canvas
          ref={canvasRef}
          width={inputResolution.width}
          height={inputResolution.height}
        />
      </div>

      {/* Simple display of face data */}
      <div style={{ 
        position: "fixed", 
        bottom: "20px",
        left: "50%",
        transform: "translateX(-50%)",
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        color: "#00f2fe",
        padding: "8px 16px",
        borderRadius: "8px",
        fontFamily: "monospace",
        fontSize: "14px",
        zIndex: 1000,
        display: "flex",
        gap: "16px",
        flexWrap: "wrap",
        justifyContent: "center",
        width: "auto",
        maxWidth: "90%"
      }}>
        <div>
          Position: x={faceData.position.x}, y={faceData.position.y}
        </div>
        <div>
          Direction: H:{faceData.horizontalDirection.toUpperCase()} V:{faceData.verticalDirection.toUpperCase()}
          {getKeyPressText()}
        </div>
        <div>
          Angles: yaw={faceData.direction.yaw}°, turn={faceData.direction.turn}°
        </div>
      </div>
    </div>
  );
}

export default FaceDirectionComponent;
