import React, { useState, useEffect, useRef } from 'react';
import * as faceapi from 'face-api.js';

const FaceDetection = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [faceDetectionActive, setFaceDetectionActive] = useState(false);
  const [facePositions, setFacePositions] = useState([]);
  const [faceDetectionInitialized, setFaceDetectionInitialized] = useState(false);
  const detectInterval = useRef(null);

  // Initialize face detection models
  const initializeFaceDetection = async () => {
    try {
      // Use local models from public/models directory
      const MODEL_URL = '/models';
      
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
      ]);
      
      setFaceDetectionInitialized(true);
      console.log('Face detection models loaded successfully');
    } catch (error) {
      console.error('Error loading face detection models:', error);
    }
  };

  // Start webcam and face detection
  const startFaceDetection = async () => {
    if (!faceDetectionInitialized) {
      await initializeFaceDetection();
    }
    
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setFaceDetectionActive(true);
        
        // Start detecting faces
        detectInterval.current = setInterval(detectFaces, 100);
      }
    } catch (error) {
      console.error('Error accessing webcam:', error);
    }
  };

  // Stop face detection
  const stopFaceDetection = () => {
    if (videoRef.current && videoRef.current.srcObject) {
      const tracks = videoRef.current.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      videoRef.current.srcObject = null;
    }
    
    if (detectInterval.current) {
      clearInterval(detectInterval.current);
      detectInterval.current = null;
    }
    
    setFaceDetectionActive(false);
    setFacePositions([]);
  };

  // Detect faces and determine position
  const detectFaces = async () => {
    if (!videoRef.current || !canvasRef.current || !faceDetectionInitialized) return;
    
    const video = videoRef.current;
    const canvas = canvasRef.current;
    
    if (video.paused || video.ended || !video.videoWidth) return;
    
    try {
      // Set canvas dimensions to match video
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      
      // Detect faces with optimal parameters
      const detections = await faceapi.detectAllFaces(
        video, 
        new faceapi.TinyFaceDetectorOptions({ scoreThreshold: 0.5 })
      );
      
      // Draw detections on canvas
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      
      // Draw midline for reference
      ctx.beginPath();
      ctx.strokeStyle = 'rgba(255, 255, 255, 0.5)';
      ctx.setLineDash([5, 5]);
      ctx.moveTo(canvas.width / 2, 0);
      ctx.lineTo(canvas.width / 2, canvas.height);
      ctx.stroke();
      ctx.setLineDash([]);
      
      // Determine positions and store them
      const positions = detections.map(detection => {
        const box = detection.box;
        const centerX = box.x + box.width / 2;
        const position = centerX < canvas.width / 2 ? 'left' : 'right';
        const distanceFromCenter = Math.abs(centerX - (canvas.width / 2));
        const percentageFromCenter = Math.round((distanceFromCenter / (canvas.width / 2)) * 100);
        
        // Draw rectangle around face
        ctx.strokeStyle = position === 'left' ? '#00ff00' : '#0000ff';
        ctx.lineWidth = 3;
        ctx.strokeRect(box.x, box.y, box.width, box.height);
        
        // Label the position
        ctx.font = '16px Arial';
        ctx.fillStyle = position === 'left' ? '#00ff00' : '#0000ff';
        ctx.fillText(`${position} (${percentageFromCenter}%)`, box.x, box.y - 5);
        
        return {
          id: Math.random().toString(36).substring(7),
          position,
          percentageFromCenter,
          box
        };
      });
      
      setFacePositions(positions);
    } catch (error) {
      console.error('Error during face detection:', error);
    }
  };

  // Toggle face detection on/off
  const toggleFaceDetection = () => {
    if (faceDetectionActive) {
      stopFaceDetection();
    } else {
      startFaceDetection();
    }
  };

  // Load face detection models on component mount
  useEffect(() => {
    initializeFaceDetection();
    
    // Cleanup on unmount
    return () => {
      if (detectInterval.current) {
        clearInterval(detectInterval.current);
      }
      stopFaceDetection();
    };
  }, []);

  return (
    <div className="face-detection-container">
      <button 
        className={`face-detection-toggle ${faceDetectionActive ? 'active' : ''}`}
        onClick={toggleFaceDetection}
      >
        {faceDetectionActive ? 'Stop Face Detection' : 'Start Face Detection'}
      </button>
      
      <div className="video-container" style={{ display: faceDetectionActive ? 'block' : 'none' }}>
        <video
          ref={videoRef}
          autoPlay
          muted
          playsInline
          onPlay={() => console.log('Video is playing')}
        />
        <canvas ref={canvasRef} className="detection-overlay" />
        
        <div className="face-positions">
          {facePositions.length === 0 ? (
            <p>No faces detected</p>
          ) : (
            <div>
              <p>Detected {facePositions.length} face(s):</p>
              <ul>
                {facePositions.map((face) => (
                  <li key={face.id} className={`face-${face.position}`}>
                    Face on the {face.position} side
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default FaceDetection;
