import React, { useState, useEffect, useRef } from 'react';
import { RetellWebClient } from "retell-client-js-sdk";
import SplineLoader from './SplineLoader';
import SplineViewer from './SplineViewer';
import './index.css';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import agentIcon from './assets/agent-icon.svg';
import userIcon from './assets/user-icon.svg';
// Import required dependencies for face detection
import "@tensorflow/tfjs";
import "@tensorflow/tfjs-backend-webgl";
import "@mediapipe/face_mesh";
import Webcam from "react-webcam";
// Import the FaceDirectionDetector component
import FaceDirectionDetector from './simple/FaceDirectionDetector';

function App() {
  // Add a ref to maintain the Spline instance
  const splineInstanceRef = useRef(null);
  const [isCallActive, setIsCallActive] = useState(false);
  const [retellWebClient, setRetellWebClient] = useState(null);
  const [eventStatus, setEventStatus] = useState({
    callStarted: false,
    callEnded: false,
    agentStartTalking: false,
    agentStopTalking: false,
    error: null,
    transcript: ''
  });
  const [micPermission, setMicPermission] = useState(null);
  const [accessToken, setAccessToken] = useState('');
  const [isGeneratingToken, setIsGeneratingToken] = useState(false);
  const [callInfo, setCallInfo] = useState(null);
