import React, { useEffect, useRef } from 'react';
import Webcam from 'react-webcam';
import { useFaceDetection } from 'react-use-face-detection';

const FaceDetector = ({ onFacePositionChange, isEnabled = true }) => {
  const webcamRef = useRef(null);
  
  // Initialize face detection with MediaPipe
  const { boundingBox, webcamRef: faceDetectionWebcamRef, isLoading, detected, facesDetected } = useFaceDetection({
    faceDetectionOptions: {
      model: 'short',
    }
  });

  // Set webcam reference from face detection hook to our ref
  useEffect(() => {
    webcamRef.current = faceDetectionWebcamRef.current;
  }, [faceDetectionWebcamRef]);

  // Process bounding box data to determine face position
  useEffect(() => {
    if (!isEnabled || !detected || boundingBox.length === 0) return;

    // We'll use the first detected face
    const face = boundingBox[0];
    
    // Calculate the center of the face
    const centerX = face.topLeft[0] + (face.bottomRight[0] - face.topLeft[0]) / 2;
    const centerY = face.topLeft[1] + (face.bottomRight[1] - face.topLeft[1]) / 2;
    
    // Calculate position relative to the webcam dimensions (as percentage)
    const relativeX = centerX / 640; // Assuming 640 width
    const relativeY = centerY / 480; // Assuming 480 height
    
    // Determine which direction to move based on face position
    // We divide the screen into sections for simplicity
    let direction = null;
    
    // Horizontal position (left/right)
    if (relativeX < 0.4) {
      direction = 'l'; // Move left when face is on the right side
    } else if (relativeX > 0.6) {
      direction = 'r'; // Move right when face is on the left side
    }
    // Vertical position (up/down) - only if horizontal is not active
    else if (relativeY < 0.4) {
      direction = 'u'; // Move up when face is low
    } else if (relativeY > 0.6) {
      direction = 'd'; // Move down when face is high
    }
    
    // Call the callback with the direction if set
    if (direction && onFacePositionChange) {
      onFacePositionChange(direction, { x: relativeX, y: relativeY });
    }
  }, [boundingBox, detected, isEnabled, onFacePositionChange]);

  return (
    <div className={`face-detector ${isEnabled ? 'active' : 'inactive'}`}>
      {isEnabled && (
        <div style={{ position: 'relative' }}>
          <Webcam
            ref={faceDetectionWebcamRef}
            className="face-detection-webcam"
            mirrored={true}
            screenshotFormat="image/jpeg"
            style={{
              width: '100%',
              height: 'auto',
              maxWidth: '200px',
              borderRadius: '8px',
              opacity: 0.8,
            }}
          />
          {/* Face detection status indicator */}
          <div className="detection-status">
            {isLoading ? (
              <div className="status-indicator loading">Loading model...</div>
            ) : detected ? (
              <div className="status-indicator detected">
                {facesDetected} face{facesDetected !== 1 ? 's' : ''} detected
              </div>
            ) : (
              <div className="status-indicator not-detected">No faces detected</div>
            )}
          </div>
          {/* Debug visualization of bounding boxes */}
          <svg
            className="bounding-box-visualization"
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
            }}
          >
            {boundingBox.map((box, index) => (
              <rect
                key={index}
                x={`${box.topLeft[0] / 640 * 100}%`}
                y={`${box.topLeft[1] / 480 * 100}%`}
                width={`${(box.bottomRight[0] - box.topLeft[0]) / 640 * 100}%`}
                height={`${(box.bottomRight[1] - box.topLeft[1]) / 480 * 100}%`}
                stroke="#00FFFF"
                strokeWidth="2"
                fill="none"
              />
            ))}
          </svg>
        </div>
      )}
    </div>
  );
};

export default FaceDetector;
