import React, { useRef, useState, useEffect } from "react";
import "@tensorflow/tfjs";
import "@tensorflow/tfjs-backend-webgl";
import "@mediapipe/face_mesh";
import Webcam from "react-webcam";
import FaceDirectionDetector from "./face_detection/FaceDirectionDetector";

const inputResolution = {
  width: 640,
  height: 480,
};

const videoConstraints = {
  width: inputResolution.width,
  height: inputResolution.height,
  facingMode: "user",
};

function FaceDirectionComponent() {
  const canvasRef = useRef(null);
  const webcamRef = useRef(null);
  const [loaded, setLoaded] = useState(false);
  const [detector, setDetector] = useState(null);
  const [faceData, setFaceData] = useState({
    position: { x: 0, y: 0 },
    direction: { yaw: 0, turn: 0 },
    horizontalDirection: "center"
  });

  // Get horizontal direction based on yaw angle
  const getHorizontalDirection = (yaw) => {
    // Using yaw angle for left/right determination
    if (yaw < -15) {
      return "right"; // Looking right from camera perspective
    } else if (yaw > 15) {
      return "left"; // Looking left from camera perspective
    } else {
      return "center";
    }
  };

  // Initialize the detector
  useEffect(() => {
    const initializeDetector = async () => {
      const options = {
        showMesh: false,
        showTagNumbers: false,
        showDirection: false
      };
      const faceDetector = new FaceDirectionDetector(options);
      setDetector(faceDetector);
    };

    initializeDetector();

    // Cleanup on unmount
    return () => {
      if (detector) {
        detector.stop();
      }
    };
  }, []);

  // Handle video load
  const handleVideoLoad = async (videoNode) => {
    const video = videoNode.target;
    if (video.readyState !== 4) return;
    if (loaded) return;

    if (detector && webcamRef.current && canvasRef.current) {
      await detector.start(
        webcamRef.current.video, 
        canvasRef.current, 
        (data) => {
          // Update face data when detection occurs
          if (data && data.keypoints && data.keypoints[1]) {
            const yaw = data.yaw || 0;
            const horizontalDirection = getHorizontalDirection(yaw);

            setFaceData({
              position: {
                x: Math.round(data.keypoints[1].x),
                y: Math.round(data.keypoints[1].y)
              },
              direction: {
                yaw: Math.round(yaw),
                turn: Math.round(data.turn || 0)
              },
              horizontalDirection
            });
          }
        }
      );
      setLoaded(true);
    }
  };

  // Get color based on direction
  const getDirectionColor = (direction) => {
    switch(direction) {
      case "left": return "#ff6b6b";
      case "right": return "#00a8ff";
      default: return "#00f2fe";
    }
  };

  return (
    <div>
      {/* Hidden webcam and canvas for detection */}
      <div style={{ position: "absolute", visibility: "hidden" }}>
        <Webcam
          ref={webcamRef}
          width={inputResolution.width}
          height={inputResolution.height}
          videoConstraints={videoConstraints}
          onLoadedData={handleVideoLoad}
        />

        <canvas
          ref={canvasRef}
          width={inputResolution.width}
          height={inputResolution.height}
        />
      </div>

      {/* Simple display of face data */}
      <div style={{ 
        position: "fixed", 
        bottom: "20px",
        left: "50%",
        transform: "translateX(-50%)",
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        color: "#00f2fe",
        padding: "8px 16px",
        borderRadius: "8px",
        fontFamily: "monospace",
        fontSize: "14px",
        zIndex: 1000,
        display: "flex",
        gap: "16px",
        alignItems: "center"
      }}>
        <div>
          Position: x={faceData.position.x}, y={faceData.position.y}
        </div>
        <div style={{ 
          display: "flex", 
          alignItems: "center", 
          gap: "8px" 
        }}>
          Direction: 
          <span style={{ 
            color: getDirectionColor(faceData.horizontalDirection),
            fontWeight: "bold"
          }}>
            {faceData.horizontalDirection.toUpperCase()}
          </span>
          (yaw: {faceData.direction.yaw}Â°)
        </div>
        <div style={{
          width: "16px",
          height: "16px",
          borderRadius: "50%",
          backgroundColor: getDirectionColor(faceData.horizontalDirection)
        }} />
      </div>
    </div>
  );
}

export default FaceDirectionComponent;
