import React, { useState, useEffect } from 'react';
import { RetellWebClient } from "retell-client-js-sdk";

function App() {
  const [isCallActive, setIsCallActive] = useState(false);
  const [retellWebClient, setRetellWebClient] = useState(null);
  const [eventStatus, setEventStatus] = useState({
    callStarted: false,
    callEnded: false,
    agentStartTalking: false,
    agentStopTalking: false,
    error: null,
    transcript: ''
  });
  const [micPermission, setMicPermission] = useState(null);

  useEffect(() => {
    // Initialize the RetellWebClient
    const client = new RetellWebClient();
    setRetellWebClient(client);

    // Check for microphone permissions
    checkMicrophonePermission();

    // Set up event listeners
    client.on("call_started", () => {
      console.log("call started");
      setEventStatus(prev => ({ ...prev, callStarted: true, callEnded: false }));
    });

    client.on("call_ended", () => {
      console.log("call ended");
      setIsCallActive(false);
      setEventStatus(prev => ({ ...prev, callEnded: true }));
    });

    client.on("agent_start_talking", () => {
      console.log("agent_start_talking");
      setEventStatus(prev => ({ ...prev, agentStartTalking: true, agentStopTalking: false }));
    });

    client.on("agent_stop_talking", () => {
      console.log("agent_stop_talking");
      setEventStatus(prev => ({ ...prev, agentStopTalking: true, agentStartTalking: false }));
    });

    client.on("update", (update) => {
      if (update.transcript) {
        setEventStatus(prev => ({ ...prev, transcript: update.transcript }));
      }
    });

    client.on("metadata", (metadata) => {
      // console.log(metadata);
    });

    client.on("error", (error) => {
      console.error("An error occurred:", error);
      setEventStatus(prev => ({ ...prev, error: error.message || "Unknown error" }));
      client.stopCall();
    });

    // Clean up event listeners on component unmount
    return () => {
      if (client) {
        client.stopCall();
      }
    };
  }, []);

  const checkMicrophonePermission = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setMicPermission('granted');
      // Stop the stream tracks immediately
      stream.getTracks().forEach(track => track.stop());
    } catch (error) {
      console.error('Microphone permission denied:', error);
      setMicPermission('denied');
    }
  };

  const requestMicrophonePermission = async () => {
    try {
      await navigator.mediaDevices.getUserMedia({ audio: true });
      setMicPermission('granted');
      return true;
    } catch (error) {
      console.error('Failed to get microphone permission:', error);
      setMicPermission('denied');
      return false;
    }
  };

  const startCall = async () => {
    if (!retellWebClient || isCallActive) return;

    // First ensure we have microphone permissions
    if (micPermission !== 'granted') {
      const permissionGranted = await requestMicrophonePermission();
      if (!permissionGranted) {
        alert('Microphone permission is required to start a call');
        return;
      }
    }

    try {
      // Reset event status
      setEventStatus({
        callStarted: false,
        callEnded: false,
        agentStartTalking: false,
        agentStopTalking: false,
        error: null,
        transcript: ''
      });

      // In a real application, you would make an API call to get a valid access token
      // For now, we'll use a placeholder
      const accessToken = "your_access_token_here";

      // Use the agent ID provided
      const agentId = "agent_30b1b504a89d37e3109a7da3e9";

      // Make a direct call with agentId instead of using an access token
      await retellWebClient.startCall({
        // Use direct calling with agent ID instead of access token
        apiKey: "key_c658f6ca7a8848afb60a7895641f",
        agentId: agentId,
        sampleRate: 24000,
        captureDeviceId: "default", // This will use the default microphone
        // We'll let the browser handle playback device selection
        emitRawAudioSamples: false,
      });

      setIsCallActive(true);
    } catch (error) {
      console.error("Failed to start call:", error);
      setEventStatus(prev => ({ ...prev, error: error.message || "Failed to start call" }));
    }
  };

  const stopCall = () => {
    if (retellWebClient && isCallActive) {
      retellWebClient.stopCall();
      setIsCallActive(false);
    }
  };

  return (
    <div className="App" style={{ textAlign: 'center', padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1>Retell Voice Demo</h1>

      <div style={{ margin: '20px 0', padding: '10px', backgroundColor: '#f5f5f5', borderRadius: '4px' }}>
        <h3>Microphone Permission: {micPermission === 'granted' ? '‚úÖ Granted' : micPermission === 'denied' ? '‚ùå Denied' : '‚ùì Unknown'}</h3>
        {micPermission !== 'granted' && (
          <button 
            onClick={requestMicrophonePermission}
            style={{ 
              padding: '8px 15px', 
              backgroundColor: '#2196F3',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Request Mic Permission
          </button>
        )}
      </div>

      <div style={{ margin: '20px 0' }}>
        <button 
          onClick={startCall} 
          disabled={isCallActive}
          style={{ 
            padding: '10px 20px', 
            margin: '0 10px', 
            backgroundColor: isCallActive ? '#ccc' : '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: isCallActive ? 'default' : 'pointer'
          }}
        >
          Start Call with Agent
        </button>
        <button 
          onClick={stopCall} 
          disabled={!isCallActive}
          style={{ 
            padding: '10px 20px', 
            margin: '0 10px',
            backgroundColor: !isCallActive ? '#ccc' : '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: !isCallActive ? 'default' : 'pointer'
          }}
        >
          Stop Call
        </button>
      </div>

      <div style={{ margin: '20px 0', padding: '15px', border: '1px solid #ddd', borderRadius: '4px', textAlign: 'left' }}>
        <h3>Call Status: {isCallActive ? 'üü¢ Active' : 'üî¥ Inactive'}</h3>
        <div style={{ marginTop: '10px' }}>
          <p><strong>Agent ID:</strong> agent_30b1b504a89d37e3109a7da3e9</p>
        </div>
        <div style={{ marginTop: '10px' }}>
          <p><strong>Event Status:</strong></p>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            <li>üé§ Call Started: {eventStatus.callStarted ? '‚úÖ Yes' : '‚ùå No'}</li>
            <li>üõë Call Ended: {eventStatus.callEnded ? '‚úÖ Yes' : '‚ùå No'}</li>
            <li>üó£Ô∏è Agent Talking: {eventStatus.agentStartTalking && !eventStatus.agentStopTalking ? '‚úÖ Yes' : '‚ùå No'}</li>
            {eventStatus.error && <li style={{ color: 'red' }}>‚ö†Ô∏è Error: {eventStatus.error}</li>}
          </ul>
        </div>
        {eventStatus.transcript && (
          <div style={{ marginTop: '10px', maxHeight: '150px', overflowY: 'auto', padding: '10px', backgroundColor: '#f9f9f9', borderRadius: '4px' }}>
            <p><strong>Latest Transcript:</strong></p>
            <p style={{ whiteSpace: 'pre-line' }}>{eventStatus.transcript}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
