import React, { useRef, useState, useEffect } from "react";
import "@tensorflow/tfjs";
import "@tensorflow/tfjs-backend-webgl";
import "@mediapipe/face_mesh";
import Webcam from "react-webcam";
import FaceDirectionDetector from "./face_detection/FaceDirectionDetector";

const inputResolution = {
  width: 640,
  height: 480,
};

const videoConstraints = {
  width: inputResolution.width,
  height: inputResolution.height,
  facingMode: "user",
};

function FaceDirectionComponent() {
  const canvasRef = useRef(null);
  const webcamRef = useRef(null);
  const [loaded, setLoaded] = useState(false);
  const [detector, setDetector] = useState(null);
  const [faceData, setFaceData] = useState({
    position: { x: 0, y: 0 },
    direction: { yaw: 0, turn: 0 }
  });

  // Calculate direction labels based on yaw and turn angles
  const getDirectionLabel = () => {
    const { yaw, turn } = faceData.direction;

    // Thresholds for determining direction
    const YAW_THRESHOLD = 25; // Threshold for left/right detection
    const TURN_THRESHOLD = 25; // Threshold for up/down detection

    let horizontalDirection = "Center";
    let verticalDirection = "Center";

    // Determine horizontal direction (left/right)
    if (yaw > YAW_THRESHOLD) {
      horizontalDirection = "Left";
    } else if (yaw < -YAW_THRESHOLD) {
      horizontalDirection = "Right";
    }

    // Determine vertical direction (up/down)
    if (turn > TURN_THRESHOLD) {
      verticalDirection = "Down";
    } else if (turn < -TURN_THRESHOLD) {
      verticalDirection = "Up";
    }

    // If both directions are center, return just "Center"
    if (horizontalDirection === "Center" && verticalDirection === "Center") {
      return "Center";
    }

    // If one direction is center, return just the other direction
    if (horizontalDirection === "Center") {
      return verticalDirection;
    }
    if (verticalDirection === "Center") {
      return horizontalDirection;
    }

    // Otherwise return the combination
    return `${verticalDirection}-${horizontalDirection}`;
  };

  // Initialize the detector
  useEffect(() => {
    const initializeDetector = async () => {
      const options = {
        showMesh: false,
        showTagNumbers: false,
        showDirection: false
      };
      const faceDetector = new FaceDirectionDetector(options);
      setDetector(faceDetector);
    };

    initializeDetector();

    // Cleanup on unmount
    return () => {
      if (detector) {
        detector.stop();
      }
    };
  }, []);

  // Handle video load
  const handleVideoLoad = async (videoNode) => {
    const video = videoNode.target;
    if (video.readyState !== 4) return;
    if (loaded) return;

    if (detector && webcamRef.current && canvasRef.current) {
      await detector.start(
        webcamRef.current.video, 
        canvasRef.current, 
        (data) => {
          // Update face data when detection occurs
          if (data && data.keypoints && data.keypoints[1]) {
            setFaceData({
              position: {
                x: Math.round(data.keypoints[1].x),
                y: Math.round(data.keypoints[1].y)
              },
              direction: {
                yaw: Math.round(data.yaw || 0),
                turn: Math.round(data.turn || 0)
              }
            });
          }
        }
      );
      setLoaded(true);
    }
  };

  // Get direction label
  const directionLabel = getDirectionLabel();

  // Get color based on direction
  const getDirectionColor = () => {
    if (directionLabel === "Center") return "#00f2fe";
    if (directionLabel.includes("Left")) return "#ff6b6b";
    if (directionLabel.includes("Right")) return "#5352ed";
    if (directionLabel.includes("Up")) return "#00a8ff";
    if (directionLabel.includes("Down")) return "#ff9f43";
    return "#00f2fe";
  };

  return (
    <div>
      {/* Hidden webcam and canvas for detection */}
      <div style={{ position: "absolute", visibility: "hidden" }}>
        <Webcam
          ref={webcamRef}
          width={inputResolution.width}
          height={inputResolution.height}
          videoConstraints={videoConstraints}
          onLoadedData={handleVideoLoad}
        />

        <canvas
          ref={canvasRef}
          width={inputResolution.width}
          height={inputResolution.height}
        />
      </div>

      {/* Enhanced display of face data with direction indicator */}
      <div style={{ 
        position: "fixed", 
        bottom: "20px",
        left: "50%",
        transform: "translateX(-50%)",
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        color: "#00f2fe",
        padding: "8px 16px",
        borderRadius: "8px",
        fontFamily: "monospace",
        fontSize: "14px",
        zIndex: 1000,
        display: "flex",
        gap: "16px",
        alignItems: "center"
      }}>
        <div>
          Position: x={faceData.position.x}, y={faceData.position.y}
        </div>
        <div>
          Angles: yaw={faceData.direction.yaw}°, turn={faceData.direction.turn}°
        </div>
        <div style={{
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          padding: "4px 12px",
          borderRadius: "4px",
          color: getDirectionColor(),
          fontWeight: "bold"
        }}>
          Direction: {directionLabel}
        </div>
      </div>
    </div>
  );
}

export default FaceDirectionComponent;
