import React, { useRef, useState, useEffect } from "react";
import "@tensorflow/tfjs";
import "@tensorflow/tfjs-backend-webgl";
import "@mediapipe/face_mesh";
import Webcam from "react-webcam";
import FaceDirectionDetector from "./face_detection/FaceDirectionDetector";

const inputResolution = {
  width: 640,
  height: 480,
};

const videoConstraints = {
  width: inputResolution.width,
  height: inputResolution.height,
  facingMode: "user",
};

function FaceDirectionComponent() {
  const canvasRef = useRef(null);
  const webcamRef = useRef(null);
  const [loaded, setLoaded] = useState(false);
  const [detector, setDetector] = useState(null);
  const [faceData, setFaceData] = useState({
    position: { x: 0, y: 0 },
    direction: { yaw: 0, turn: 0 }
  });

  // Initialize the detector
  useEffect(() => {
    const initializeDetector = async () => {
      const options = {
        showMesh: false,
        showTagNumbers: false,
        showDirection: false
      };
      const faceDetector = new FaceDirectionDetector(options);
      setDetector(faceDetector);
    };

    initializeDetector();

    // Cleanup on unmount
    return () => {
      if (detector) {
        detector.stop();
      }
    };
  }, []);

  // Handle video load
  const handleVideoLoad = async (videoNode) => {
    const video = videoNode.target;
    if (video.readyState !== 4) return;
    if (loaded) return;

    if (detector && webcamRef.current && canvasRef.current) {
      await detector.start(
        webcamRef.current.video, 
        canvasRef.current, 
        (data) => {
          // Update face data when detection occurs
          if (data && data.keypoints && data.keypoints[1]) {
            setFaceData({
              position: {
                x: Math.round(data.keypoints[1].x),
                y: Math.round(data.keypoints[1].y)
              },
              direction: {
                yaw: Math.round(data.yaw || 0),
                turn: Math.round(data.turn || 0)
              }
            });
          }
        }
      );
      setLoaded(true);
    }
  };

  return (
    <div>
      {/* Hidden webcam and canvas for detection */}
      <div style={{ position: "absolute", visibility: "hidden" }}>
        <Webcam
          ref={webcamRef}
          width={inputResolution.width}
          height={inputResolution.height}
          videoConstraints={videoConstraints}
          onLoadedData={handleVideoLoad}
        />
        
        <canvas
          ref={canvasRef}
          width={inputResolution.width}
          height={inputResolution.height}
        />
      </div>

      {/* Simple display of face data */}
      <div style={{ 
        position: "fixed", 
        bottom: "20px",
        left: "50%",
        transform: "translateX(-50%)",
        backgroundColor: "rgba(0, 0, 0, 0.7)",
        color: "#00f2fe",
        padding: "8px 16px",
        borderRadius: "8px",
        fontFamily: "monospace",
        fontSize: "14px",
        zIndex: 1000,
        display: "flex",
        gap: "16px"
      }}>
        <div>
          Position: x={faceData.position.x}, y={faceData.position.y}
        </div>
        <div>
          Direction: yaw={faceData.direction.yaw}°, turn={faceData.direction.turn}°
        </div>
      </div>
    </div>
  );
}

export default FaceDirectionComponent;
