import React, { useState, useEffect, useRef } from 'react';
import { RetellWebClient } from "retell-client-js-sdk";
import SplineLoader from './SplineLoader';
import SplineViewer from './SplineViewer';
import './index.css';

function App() {
  // Add a ref to maintain the Spline instance
  const splineInstanceRef = useRef(null);
  const cursorAnimationRef = useRef(null);
  const [isCallActive, setIsCallActive] = useState(false);
  const [retellWebClient, setRetellWebClient] = useState(null);
  const [eventStatus, setEventStatus] = useState({
    callStarted: false,
    callEnded: false,
    agentStartTalking: false,
    agentStopTalking: false,
    error: null,
    transcript: ''
  });
  const [micPermission, setMicPermission] = useState(null);
  const [accessToken, setAccessToken] = useState('');
  const [isGeneratingToken, setIsGeneratingToken] = useState(false);
  const [callInfo, setCallInfo] = useState(null);
  const [loadingComplete, setLoadingComplete] = useState(false);
  const [showUI, setShowUI] = useState(true);
  // Add state to control the mounting/unmounting of Spline
  const [splineInitialized, setSplineInitialized] = useState(false);
  const [cursorPosition, setCursorPosition] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });
  const [isCursorControlled, setIsCursorControlled] = useState(false);
  const [realCursorPosition, setRealCursorPosition] = useState({ x: window.innerWidth / 2, y: window.innerHeight / 2 });
  const [isUserMovingCursor, setIsUserMovingCursor] = useState(false);
  const userCursorTimeoutRef = useRef(null);

  // Agent ID for Retell
  const AGENT_ID = "agent_30b1b504a89d37e3109a7da3e9";

  // Server endpoint for token generation
  const TOKEN_SERVER_URL = "http://127.0.0.1:5000/api/generate-token";

  // Track real user cursor movements
  useEffect(() => {
    const handleMouseMove = (event) => {
      const { clientX, clientY } = event;
      setRealCursorPosition({ x: clientX, y: clientY });

      // When user moves cursor, use real position and stop simulated movement temporarily
      if (isCursorControlled) {
        setIsUserMovingCursor(true);
        setCursorPosition({ x: clientX, y: clientY });

        // Clear any existing timeout
        if (userCursorTimeoutRef.current) {
          clearTimeout(userCursorTimeoutRef.current);
        }

        // Set a timeout to return to simulated movement if user stops moving cursor
        userCursorTimeoutRef.current = setTimeout(() => {
          setIsUserMovingCursor(false);
        }, 3000);
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      if (userCursorTimeoutRef.current) {
        clearTimeout(userCursorTimeoutRef.current);
      }
    };
  }, [isCursorControlled]);

  // Control cursor movement via Spline
  useEffect(() => {
    if (splineInitialized && splineInstanceRef.current) {
      // Create a hidden div that follows our calculated cursor position
      // for Spline's eye tracking to follow
      const createOrUpdateCursorElement = () => {
        let cursorElement = document.getElementById('virtual-cursor');
        if (!cursorElement) {
          cursorElement = document.createElement('div');
          cursorElement.id = 'virtual-cursor';
          document.body.appendChild(cursorElement);
        }
        cursorElement.style.position = 'absolute';
        cursorElement.style.left = `${cursorPosition.x}px`;
        cursorElement.style.top = `${cursorPosition.y}px`;
        cursorElement.style.width = '1px';
        cursorElement.style.height = '1px';
        cursorElement.style.pointerEvents = 'none';
        cursorElement.style.opacity = '0';
        cursorElement.style.zIndex = '9999';
      };

      createOrUpdateCursorElement();
    }
  }, [cursorPosition, splineInitialized]);

  // Add this new effect to handle starting/stopping animations based on agent talking state
  useEffect(() => {
    if (eventStatus.agentStartTalking && !eventStatus.agentStopTalking && isCallActive) {
      // Use a function reference that has access to the latest state
      const startAnimation = () => {
        if (cursorAnimationRef.current) {
          cancelAnimationFrame(cursorAnimationRef.current);
        }

        let time = 0;
        const duration = 5000; // animation cycle duration in ms

        // Generate random parameters for this animation cycle to vary the movements
        const randomAmplitude = {
          nod: 40 + Math.random() * 20,      // Random amplitude between 40-60
          shake: 60 + Math.random() * 40,     // Random amplitude between 60-100
          circleX: 50 + Math.random() * 40,   // Random amplitude between 50-90
          circleY: 30 + Math.random() * 20    // Random amplitude between 30-50
        };

        const randomSpeed = {
          nod: 8 + Math.random() * 4,         // Random speed between 8-12
          shake: 4 + Math.random() * 2,       // Random speed between 4-6
          circle: 3 + Math.random() * 2       // Random speed between 3-5
        };

        // Start from current cursor position or center if not determined
        const startX = isUserMovingCursor ? realCursorPosition.x : window.innerWidth / 2;
        const startY = isUserMovingCursor ? realCursorPosition.y : window.innerHeight / 2;

        const animate = () => {
          // Get latest state within the animation frame
          if (!eventStatus.agentStartTalking || eventStatus.agentStopTalking || !isCallActive) {
            // Stop animation if agent is not talking
            setIsCursorControlled(false);
            return;
          }

          time += 16; // approx 60fps
          if (time > duration) {
            time = 0; // reset animation cycle
          }

          // Define animation patterns for talking
          const progress = time / duration;

          // Create different movement patterns with randomized parameters
          const noddingPattern = Math.sin(progress * Math.PI * randomSpeed.nod) * randomAmplitude.nod;
          const shakingPattern = Math.sin(progress * Math.PI * randomSpeed.shake) * randomAmplitude.shake;
          const circlePattern = {
            x: Math.cos(progress * Math.PI * randomSpeed.circle) * randomAmplitude.circleX,
            y: Math.sin(progress * Math.PI * randomSpeed.circle) * randomAmplitude.circleY
          };

          // Choose which pattern to use based on progress and add randomness
          let x, y;

          // Add some randomness to the pattern selection
          const patternSelector = progress + (Math.random() * 0.05); // Add small random variation

          if (patternSelector < 0.33) {
            // Nodding (up/down)
            x = startX;
            y = startY + noddingPattern;
          } else if (patternSelector < 0.66) {
            // Shaking head (left/right)
            x = startX + shakingPattern;
            y = startY;
          } else {
            // Circular/oval movement
            x = startX + circlePattern.x;
            y = startY + circlePattern.y;
          }

          // Add subtle random jitter for more natural movement
          x += (Math.random() - 0.5) * 5;
          y += (Math.random() - 0.5) * 5;

          // Keep cursor within viewport bounds
          x = Math.max(0, Math.min(window.innerWidth, x));
          y = Math.max(0, Math.min(window.innerHeight, y));

          // Update cursor position for Spline to follow
          if (!isUserMovingCursor) {
            setCursorPosition({ x, y });
          }

          // Continue animation
          cursorAnimationRef.current = requestAnimationFrame(animate);
        };

        setIsCursorControlled(true);
        cursorAnimationRef.current = requestAnimationFrame(animate);
      };

      // Start the animation
      startAnimation();
    } else if (!eventStatus.agentStartTalking || eventStatus.agentStopTalking || !isCallActive) {
      if (cursorAnimationRef.current) {
        cancelAnimationFrame(cursorAnimationRef.current);
        setIsCursorControlled(false);
      }
    }

    // Clean up when component unmounts or dependencies change
    return () => {
      if (cursorAnimationRef.current) {
        cancelAnimationFrame(cursorAnimationRef.current);
      }
    };
  }, [eventStatus.agentStartTalking, eventStatus.agentStopTalking, isCallActive, isUserMovingCursor, realCursorPosition]);

  useEffect(() => {
    // Initialize the RetellWebClient
    const client = new RetellWebClient();
    setRetellWebClient(client);

    // Check for microphone permissions
    checkMicrophonePermission();

    // Set up event listeners
    client.on("call_started", () => {
      console.log("call started");
      setEventStatus(prev => ({ ...prev, callStarted: true, callEnded: false }));
    });

    client.on("call_ended", () => {
      console.log("call ended");
      setIsCallActive(false);
      setEventStatus(prev => ({ ...prev, callEnded: true }));
    });

    client.on("agent_start_talking", () => {
      console.log("agent_start_talking");
      setEventStatus(prev => ({ ...prev, agentStartTalking: true, agentStopTalking: false }));
    });

    client.on("agent_stop_talking", () => {
      console.log("agent_stop_talking");
      setEventStatus(prev => ({ ...prev, agentStopTalking: true, agentStartTalking: false }));
    });

    client.on("update", (update) => {
      console.log("Update received:", update);
      if (update.transcript) {
        // Check if transcript is an object with role and content properties
        if (typeof update.transcript === 'object' && update.transcript.role && update.transcript.content) {
          // Extract the content text
          setEventStatus(prev => ({ ...prev, transcript: update.transcript.content }));
        } 
        // Check if transcript is an array of message objects
        else if (Array.isArray(update.transcript)) {
          // Map through messages and format them
          const formattedTranscript = update.transcript
            .map(msg => {
              if (typeof msg === 'object' && msg.role && msg.content) {
                return `${msg.role}: ${msg.content}`;
              }
              return String(msg); // Convert to string if it's some other type
            })
            .join('\n');
          setEventStatus(prev => ({ ...prev, transcript: formattedTranscript }));
        } 
        // If it's a string or can be converted to string
        else {
          setEventStatus(prev => ({ ...prev, transcript: String(update.transcript) }));
        }
      }
    });

    client.on("metadata", (metadata) => {
      console.log("Metadata received:", metadata);
    });

    client.on("error", (error) => {
      console.error("An error occurred:", error);
      setEventStatus(prev => ({ ...prev, error: error.message || "Unknown error" }));
      client.stopCall();
    });

    // Clean up event listeners on component unmount
    return () => {
      if (client) {
        client.stopCall();
      }
      if (cursorAnimationRef.current) {
        cancelAnimationFrame(cursorAnimationRef.current);
      }
      // Remove virtual cursor element
      const cursorElement = document.getElementById('virtual-cursor');
      if (cursorElement) {
        document.body.removeChild(cursorElement);
      }
    };
  }, []);

  // Function to generate an access token from the server
  const generateAccessToken = async () => {
    try {
      setIsGeneratingToken(true);
      setEventStatus(prev => ({ ...prev, error: null }));
      console.log("Requesting access token from server...");

      // Make the API call to our server endpoint
      const response = await fetch(TOKEN_SERVER_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          agent_id: AGENT_ID,
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || `Server responded with status: ${response.status}`);
      }

      const data = await response.json();

      if (!data.access_token) {
        throw new Error("No access token received from server");
      }

      console.log("Access token received from server:", data.access_token);
      setAccessToken(data.access_token);
      setCallInfo(data); // Store the full call info for reference
      setIsGeneratingToken(false);

      return data.access_token;
    } catch (error) {
      console.error("Failed to generate access token:", error);
      setEventStatus(prev => ({ ...prev, error: error.message || "Failed to generate access token" }));
      setIsGeneratingToken(false);
      return null;
    }
  };

  const checkMicrophonePermission = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      setMicPermission('granted');
      // Stop the stream tracks immediately
      stream.getTracks().forEach(track => track.stop());
    } catch (error) {
      console.error('Microphone permission denied:', error);
      setMicPermission('denied');
    }
  };

  const requestMicrophonePermission = async () => {
    try {
      await navigator.mediaDevices.getUserMedia({ audio: true });
      setMicPermission('granted');
      return true;
    } catch (error) {
      console.error('Failed to get microphone permission:', error);
      setMicPermission('denied');
      return false;
    }
  };

  const startCall = async () => {
    if (!retellWebClient || isCallActive) return;

    // First ensure we have microphone permissions
    if (micPermission !== 'granted') {
      const permissionGranted = await requestMicrophonePermission();
      if (!permissionGranted) {
        alert('Microphone permission is required to start a call');
        return;
      }
    }

    try {
      // Reset event status
      setEventStatus({
        callStarted: false,
        callEnded: false,
        agentStartTalking: false,
        agentStopTalking: false,
        error: null,
        transcript: ''
      });

      // Ensure we have a valid token
      if (!accessToken) {
        const token = await generateAccessToken();
        if (!token) {
          throw new Error("Failed to generate a valid access token");
        }
      }

      console.log("Starting call with access token...");

      // Start the call using the access token
      await retellWebClient.startCall({
        accessToken: accessToken,
        sampleRate: 24000,
        captureDeviceId: "default",
        emitRawAudioSamples: false,
      });

      setIsCallActive(true);
    } catch (error) {
      console.error("Failed to start call:", error);
      setEventStatus(prev => ({ ...prev, error: error.message || "Failed to start call" }));
    }
  };

  const stopCall = () => {
    if (retellWebClient && isCallActive) {
      retellWebClient.stopCall();
      setIsCallActive(false);
    }
  };

  // Function to check if token is expired (tokens are valid for 30 seconds)
  const isTokenExpired = () => {
    // If we don't have token info, consider it expired
    if (!accessToken || !callInfo) return true;

    // Track when we received the token in our application
    if (!callInfo._tokenReceivedTime) {
      // If this is the first time checking, add a timestamp of when we received it
      callInfo._tokenReceivedTime = Date.now();
      setCallInfo({...callInfo}); // Update the state to save this timestamp
      return false; // Token is fresh
    }

    // Calculate how long ago we received the token
    const tokenReceivedTime = callInfo._tokenReceivedTime;
    const currentTime = Date.now();
    const tokenAgeInSeconds = (currentTime - tokenReceivedTime) / 1000;

    // Token expires after 30 seconds, but we consider it expired at 25s
    // to give us a buffer to generate a new one
    return tokenAgeInSeconds > 25;
  };

  // New function to handle loading completion and initialize Spline only once
  const handleLoadingComplete = () => {
    setLoadingComplete(true);
    setSplineInitialized(true);
  };

  return (
    <div className={`App ${isCursorControlled && !isUserMovingCursor ? 'cursor-controlled' : ''}`}>
      {!loadingComplete ? (
        <SplineLoader onLoadingComplete={handleLoadingComplete} />
      ) : (
        <>
          {/* Main Spline Animation filling the entire viewport */}
          <div className="spline-container">
            {/* Use splineInitialized to ensure it's only mounted once */}
            {splineInitialized && (
              <SplineViewer 
                ref={splineInstanceRef}
                url="https://prod.spline.design/8CIsvX5hg8u-JUKM/scene.splinecode"
                options={{
                  hint: true,
                  'loading-anim-type': 'spinner-small-dark'
                }}
                persistOnUpdate={true}
              />
            )}
          </div>

          {/* Minimal UI sidebar */}
          <div className={`sidebar ${showUI ? 'open' : 'closed'}`}>
            <div className="toggle-ui" onClick={() => setShowUI(!showUI)}>
              {showUI ? '×' : '≡'}
            </div>

            <div className="sidebar-content">
              <h2>AI Voice</h2>

              <div className="status-indicator">
                <div className={`status-dot ${isCallActive ? 'active' : 'inactive'}`}></div>
                <span>{isCallActive ? 'Connected' : 'Disconnected'}</span>
              </div>

              {micPermission !== 'granted' && (
                <button 
                  onClick={requestMicrophonePermission}
                  className="btn-secondary btn-small"
                >
                  Enable Mic
                </button>
              )}

              <div className="control-buttons">
                <button 
                  onClick={!accessToken || isTokenExpired() ? generateAccessToken : startCall} 
                  disabled={isCallActive || isGeneratingToken}
                  className="btn-primary"
                >
                  {!accessToken || isTokenExpired() 
                    ? "Connect" 
                    : isGeneratingToken 
                      ? "..." 
                      : "Start"}
                </button>

                {isCallActive && (
                  <button 
                    onClick={stopCall} 
                    className="btn-danger"
                  >
                    End
                  </button>
                )}
              </div>

              {eventStatus.error && (
                <div className="error-badge">
                  Error: {eventStatus.error}
                </div>
              )}

              {/* Minimalistic talking indicator */}
              {isCallActive && (
                <div className="talking-indicator">
                  {eventStatus.agentStartTalking && !eventStatus.agentStopTalking ? (
                    <div className="wave-animation">
                      <div className="wave"></div>
                      <div className="wave"></div>
                      <div className="wave"></div>
                      <div className="wave"></div>
                    </div>
                  ) : null}
                </div>
              )}

              {/* Transcript in a minimal scrollable area */}
              {eventStatus.transcript && (
                <div className="mini-transcript">
                  <p style={{ whiteSpace: 'pre-line' }}>{eventStatus.transcript}</p>
                </div>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
